mapper.select:
SELECT * FROM member
where member_num like concat('%',#{searchKeyword},'%') or member_id like concat('%',#{searchKeyword},'%') or member_name like concat('%',#{searchKeyword},'%') or member_join_date like concat('%',#{searchKeyword},'%') or member_status like concat('%',#{searchKeyword},'%')
ORDER BY member_isAdmin DESC, member_num DESC LIMIT #{startRow} , #{listLimit}

SELECT * FROM review r JOIN movie m ON r.review_movie_code = m.movie_code
WHERE review_num like concat('%',#{searchKeyword},'%') or review_rating like concat('%',#{searchKeyword},'%') or review_content like concat('%',#{searchKeyword},'%') or review_date like concat('%',#{searchKeyword},'%') or review_movie_code like concat('%',#{searchKeyword},'%') or member_num like concat('%',#{searchKeyword},'%') or review_movie_name_kr like concat('%',#{searchKeyword},'%')
ORDER BY member_num DESC LIMIT #{startRow} , #{listLimit}
