mapper.insert:
INSERT 
INTO member
VALUES (
	null
	, #{member_id}
	, #{preference_theater}      
	, #{member_pw}
	, #{member_name}
	, #{member_birth}
	, #{member_phonenumber}
	, '활동'
	, 0     -- 동의하면 1, 동의 안하면 0
	, '회원'
	, now()
	, 0    -- 관리자면 1, 일반 회원이면 0
)

mapper.update:
UPDATE member
SET
	preference_theater = #{preference_theater}      
	, member_pw = #{member_pw}
WHERE
	member_id = #{member_id}

mapper.select:
SELECT *
FROM member
WHERE 
	member_name = #{member_name} 
	AND member_birth = #{member_birth}
	AND member_phonenumber = #{member_phonenumber}

mapper.update:
UPDATE member
SET 
	member_pw = #{member_pw}
WHERE
	member_phonenumber = #{member_phonenumber}

mapper.select:
SELECT member_num
FROM member
WHERE member_id = #{id}
